form.script:
  $(function () {

    var partial = $( "#partial" ).val();

        $("#check_in_time").hide();
        $("#check_out_time").hide();
         $("#date_partial").hide();
        $("#check_in").show();
        $("#check_out").show(); 

});
  $(document).ready(function(){
  $("#partial").change(function(){
    var partial = $( "#partial" ).val();
    if($(this).is(":checked")){
        $("#check_in").hide();
        $("#check_out").hide();
        $("#date_partial").show();
        $("#check_in_time").show();
        $("#check_out_time").show();
    }
    else if($(this).is(":not(:checked)")){
        $("#check_in_time").hide();
        $("#date_partial").hide();
        $("#check_out_time").hide();
        $("#check_in").show();
        $("#check_out").show(); 
    }

  });

});
